const fs = require('fs');
const _this = this;
var passCount = 0;

if (fs.existsSync('resources/deathBattleTemp/actionPossibilities.json')) {
fs.readFile('resources/deathBattleTemp/actionPossibilities.json', function read(err, data) {
    if (err) {
        _this.callNextAction(cache);
    }

    var currentData = JSON.parse(data);
    var JSONPairs = [];
    for(var i in currentData){
        var val = currentData[i];
        for(var j in val){
            var sub_key = j;
            JSONPairs.push(sub_key);
        }
    }

    var JSONPairsLength = currentData.length;

    for (i = 0; i < JSONPairs.length; i++) {
        const JSONData = currentData[i][JSONPairs[i]];
        if (JSONData.hasOwnProperty('damage') && JSONData.hasOwnProperty('format') && !isNaN(JSONData.damage)) {
            passCount = passCount + 1; 
        }
    }

    if (fs.existsSync('resources/deathBattleTemp/sourceimg.png')) {
        passCount = passCount + 1;
    }

    if (passCount > currentData.length) {
        _this.storeValue("TestPass", 1, "testResult", cache);
        _this.callNextAction(cache);
    } else {
        _this.callNextAction(cache);
    }
});
} else {
   _this.callNextAction(cache);
}

